import { useEffect, useState } from 'react';
import './App.css';

import axios from "axios";
// import { get } from 'mongoose';
//Get Data
function App() {
  const [groups, setGroups] = useState(null);
  // const [note, getnote] = useState(null);

  // get all notes
  // const getNote = async () => {
  //   const allnote = await axios.get("http://localhost:4000/api/note")
  //   const data = allnote.data
  //   getnote(data)
  // }
  // get all groups
  const getGroups = async () => {
    const allGroups = await axios.get("http://localhost:4000/api/group")
    const data = allGroups.data
    setGroups(data)
  }

  useEffect(() => {
    getGroups();
    // getNote();
  }, []);




  const colors = ['#FF5733', '#33FF57', '#3357FF', '#FF33F5', '#33F5FF', '#F5FF33', '#FF8C00', '#8A2BE2'];

  //post form data

  const [form, setform] = useState({
    name: "",
    color: "#ffffffff"
  });

  const updateForm = (e) => {
    const { name, value } = e.target;

    setform({
      ...form,
      [name]: value
    });
  }

  const createGroup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/api/group", form)
      console.log(response)
      setform({
        name: "",
        color: "#FF5733"
      })
      getGroups();
    } catch (error) {
      console.log("Error details:", error.response?.data);
      console.log("Form data being sent:", form);
    }


  }




  return (
    <div className="App">
      <form onSubmit={createGroup}>
        <h1>Create a group</h1>
        <label htmlFor=''>Group name</label>
        <input type='text'
          name='name'
          value={form.name}
          onChange={updateForm}></input>
        <br />
        <label htmlFor=''>Group color</label>
        <div>
          {colors.map(color => (
            <label key={color}>
              <input
                type='radio'
                name='color'
                value={color}
                checked={form.color === color}
                onChange={updateForm}
              />
              <span style={{backgroundColor: color, width: '20px', height: '20px', display: 'inline-block'}}></span>
            </label>
          ))}
        </div>
        <br />
        <button type='submit'
          onChange={updateForm}>Create Group</button>
      </form>
      {groups && groups.map((group) => {
        return (
          <div className='app' key={group._id}>

            <h1>{group.name}</h1>
            <h2>{group.color}</h2>
            <h2>{group.updatedAt}</h2>
          </div>

        )
      })}

    </div>
  );
}

export default App;
