import { useEffect, useState } from 'react';
import './App.css';

import axios from "axios";
import Group from '../../server/models/GroupModel';

const [group,setgroup] = useState(null);
const [note,getnote] = useState(null);

// get all groups
const getGroups = async()=>{
  const allGroups = await axios.get("http://localhost:4000/api/group")
  const data = allGroups.data
  setgroup(data)
}

useEffect(()=>{getGroups()},[]);





function App() {
  return (
    <div className="App">
      {group && group.map((groups)=>{
        return (
          <div>
            <h1>{Group.name}</h1>
            <h2>{Group.color}</h2>
            <h3></h3>
          </div>

        )
      })}

    </div>
  );
}

export default App;
