import axios from 'axios';
import { useCallback, useContext, useEffect, useState } from "react"
import { NoteDataContext } from '../../../Context/NoteContext';
import { GroupDataContext } from '../../../Context/GroupContext';

const NoteRecords = () => {
    const { selectedGroup } = useContext(GroupDataContext)
    const [notes, setNotes] = useState([])
    const [loading, setloading] = useState(false)
    const [error, setError] = useState(null)

    const fetchNotes = useCallback(async () => {
        if (!selectedGroup || !selectedGroup._id) {
            setNotes([])
            return;
        }
        setloading(true);
        setError(null);

        try {

            const response = await axios.get(`http://localhost:4000/api/note/group/${selectedGroup._id}`)
            const data = response.data
            setNotes(data)
        } catch (error) {
            console.log("Error fetching notes", error);
            setError("Failed to fetch notes")
            setNotes([]);
        } finally {
            setloading(false)
        }
    }, [selectedGroup]);

    useEffect(() => {
        fetchNotes();
    }, [fetchNotes]);

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const today = new Date();
        const yesterday = new Date(today);
        yesterday.setDate(yesterday.getDate() - 1);

        const isToday = date.toDateString() === today.toDateString();
        const isYesterday = date.toDateString() === yesterday.toDateString();

        const time = date.toLocaleTimeString('en-US', {
            hour: 'numeric',
            minute: '2-digit',
            hour12: true
        });

        if (isToday) {
            return `Today ${time}`;
        } else if (isYesterday) {
            return `Yesterday ${time}`;
        } else {
            return `${date.toLocaleDateString('en-US', {
                day: 'numeric',
                month: 'short',
                year: 'numeric'
            })} ${time}`;
        }
    };

    if (loading) {
        return (
            <div className="no-notes">
                <p>Loading notes...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="no-notes">
                <p>Error: {error}</p>
                <button onClick={fetchNotes}>Try Again</button>
            </div>
        );
    }

    if (!notes.length) {
        return (
            <div className="no-notes">
                <p>No notes yet for this group. Start by adding your first note!</p>
            </div>
        );
    }
    return (
        <div className="note-records-container">
            {notes.map((note) => (
                <div key={note._id} className="note-item">
                    <div className="note-content">
                        {note.content}
                    </div>
                    <div className="note-timestamp">
                        {formatDate(note.createdAt)}
                    </div>
                </div>
            ))}

        </div>
    );
    set
}








