import { useEffect, useState } from 'react';
import './App.css';

import axios from "axios";
//Get Data
function App() {
  const [groups, setGroups] = useState(null);
  const [note, getnote] = useState(null);

  // get all notes
  const getNote = async () => {
    const allnote = await axios.get("http://localhost:4000/api/note")
    const data = al
  }
  // get all groups
  const getGroups = async () => {
    const allGroups = await axios.get("http://localhost:4000/api/group")
    const data = allGroups.data
    setGroups(data)
  }

  useEffect(() => {
    getGroups();
  }, []);

  //post data

  const [form, setform] = useState({
    name: "",
    color: "#FF5733"
  });

  const updateForm = (e) => {
    const { name, value } = e.target;

    setform({
      ...form,
      [name]: value
    });
  }

  const createGroup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/api/group", form)
      console.log(response)
      setform({
        name: "",
        color: "#FF5733"
      })
      getGroups();
    } catch (error) {
      console.log("Error details:", error.response?.data);
      console.log("Form data being sent:", form);
    }


  }




  return (
    <div className="App">
      <form onSubmit={createGroup}>
        <h1>Create a group</h1>
        <label htmlFor=''>Group name</label>
        <input type='text'
          name='name'
          value={form.name}
          onChange={updateForm}></input>
        <br />
        <label htmlFor=''>Group color</label>
        <input type='color'
          name='color'
          value={form.color}
          onChange={updateForm}></input>
        <br />
        <button type='submit'
          onChange={updateForm}>Create Group</button>
      </form>
      {groups && groups.map((group) => {
        return (
          <div className='app' key = {group._id}>

            <h1>{group.name}</h1>
            <h2>{group.color}</h2>
            <h2>{group.updatedAt}</h2>
          </div>

        )
      })}

    </div>
  );
}

export default App;
