import { useEffect, useState } from 'react';
import './App.css';

import axios from "axios";
//import Group from '../../server/models/GroupModel';


//Get Data
function App() {
  const [groups, setGroups] = useState(null);
  //const [note, getnote] = useState(null);
  // get all groups
  const getGroups = async () => {
    const allGroups = await axios.get("http://localhost:4000/api/group")
    const data = allGroups.data
    setGroups(data)
  }

  useEffect(() => {
    getGroups();
  }, []);

  //post data

  const [form,setform] = useState({
    groupName: "",
    groupColor: ""
  });

  const updateForm = (e)=>{
    const {name,value} = e.target;

    setform({
      ...form,
      [name]:value
    });
  }

  const createGroup = async(e)=>{
    e.preventDefault();
    const response = await axios.post("http://localhost:4000/api/group",form)
    console.log(response);
  }




  return (
    <div className="App">
      {groups && groups.map((group) => {
        return (
          <div className='app' key = {group._id}>
            <form onSubmit={createGroup}>
              <h1>Create a group</h1>
              <label htmlFor=''>Group name</label>
              <input type='text' name='groupName' onChange={updateForm}></input>
              <br />
              <label htmlFor=''>Group color</label>
              <input type='color' name='groupColor' onChange={updateForm}></input>
              <br />
              <button type='submit' onChange={updateForm}>Create Group</button>
            </form>
            <h1>{group.name}</h1>
            <h2>{group.color}</h2>
            <h2>{group.updatedAt}</h2>
          </div>

        )
      })}

    </div>
  );
}

export default App;
