import { useEffect, useState } from 'react';
import './App.css';

import axios from "axios";
// import { get } from 'mongoose';
//Get Data
function App() {
  const [groups, setGroups] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState(null);




  const getGroups = async () => {
    const allGroups = await axios.get("http://localhost:4000/api/group")
    const data = allGroups.data
    setGroups(data)
  }

  useEffect(() => {
    getGroups();
  }, []);













  //post form data
  const colors = ['#FF5733', '#33FF57', '#3357FF', '#FF33F5', '#33F5FF', '#F5FF33', '#FF8C00', '#8A2BE2'];
  const [form, setform] = useState({
    name: "",
    color: "#ffffffff"
  });

  const [noteForm, setNoteForm] = useState({
    groupId: "",
    content: ""
  });

  const updateForm = (e) => {
    const { name, value } = e.target;

    setform({
      ...form,
      [name]: value
    });
  }

  const updateNoteForm = (e) => {
    const { name, value } = e.target;
    setNoteForm({ ...noteForm, [name]: value });
  }




  const createGroup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/api/group", form)
      console.log(response)
      setform({
        name: "",
        color: "#FF5733"
      })
      getGroups();
    } catch (error) {
      console.log("Error details:", error.response?.data);
      console.log("Form data being sent:", form);
    }
  }

  const createNote = async (e) => {
    e.preventDefault();
    try {
      const noteData = {
        groupId: selectedGroup._id,
        content: selectedGroup.content
      }
      await axios.post("http://localhost:4000/api/note", noteData);
      setNoteForm({ content: "" })
      selectedGroup(null)
    } catch (error) {
      console.log("Error creating note:", error.response?.data);
    }

  }




  return (
    <div className="App">
      <form onSubmit={createGroup}>
        <h1>Create a group</h1>
        <label htmlFor=''>Group name</label>
        <input type='text'
          name='name'
          value={form.name}
          onChange={updateForm}></input>
        <br />
        <label htmlFor=''>Group color</label>
        <div>
          {colors.map(color => (
            <label key={color}>
              <input
                type='radio'
                name='color'
                value={color}
                checked={form.color === color}
                onChange={updateForm}
              />
              <span style={{ backgroundColor: color, width: '20px', height: '20px', display: 'inline-block' }}></span>
            </label>
          ))}
        </div>
        <br />
        <button type='submit'
          onChange={updateForm}>Create Group</button>
      </form>
      {!selectedGroup && (
        <div>
          <h2>Select a Group:</h2>
          {groups.map((group) => (
            <div key={group._id} onClick={() => setSelectedGroup(group)} style={{ cursor: 'pointer', border: '1px solid #ccc', padding: '10px', margin: '5px' }}>
              <h3>{group.name}</h3>
              <div style={{ backgroundColor: group.color, width: '20px', height: '20px' }}></div>
            </div>
          ))}
        </div>
      )}
      {selectedGroup && (
        <div>
          <h2>Add to {selectedGroup.name}</h2>
          <form onSubmit={createNote}>
            <textarea
              name='content'
              value={noteForm.content}
              onChange={updateNoteForm}
              placeholder="chai pee thi 500 ki..."
              required
            />
            <button type='submit'>Add Note</button>
          </form>
          <button onClick={() => setSelectedGroup(null)}>Back to Groups</button>
        </div>
      )}

      {/* {groups && groups.map((group) => {
        return (
          <div className='app' key={group._id}>
            <h1>{group.name}</h1>
            <h2>{group.color}</h2>
            <h2>{group.updatedAt}</h2>
          </div>

        )
      })} */}

    </div >
  );
}

export default App;
