import { useEffect, useState } from 'react';
import './App.css';

import axios from "axios";
//import Group from '../../server/models/GroupModel';

function App() {
  const [groups, setGroups] = useState(null);
  //const [note, getnote] = useState(null);
  // get all groups
  const getGroups = async () => {
    const allGroups = await axios.get("http://localhost:4000/api/group")
    const data = allGroups.data
    setGroups(data)
  }

  useEffect(() => {
    getGroups();
  }, []);
  return (
    <div className="App">
      {groups && groups.map((group) => {
        return (
          <div className='app' key = {group._id}>
            <form>
              <h1>Create a group</h1>
              <label htmlFor=''>Group name</label>
              <input type='text' name='groupName'></input>
              <br />
              <label htmlFor='groupColor'>Group color</label>
  <select name='groupColor' id='groupColor'>
    <option value='#FF5733'>Red Orange</option>
    <option value='#33FF57'>Green</option>
    <option value='#3357FF'>Blue</option>
    <option value='#FF33F5'>Pink</option>
    <option value='#33F5FF'>Cyan</option>
    <option value='#F5FF33'>Yellow</option>
    <option value='#FF8C00'>Dark Orange</option>
    <option value='#8A2BE2'>Blue Violet</option>
  </select>
              <br />
            </form>
            <h1>{group.name}</h1>
            <h2>{group.color}</h2>
            <h2>{group.updatedAt}</h2>
          </div>

        )
      })}

    </div>
  );
}

export default App;
