import React from 'react'

const GroupForm = () => {
  return (
    <form onSubmit={createGroup}>
         <h1>Create a group</h1>
         <label htmlFor=''>Group name</label>
         <input type='text'
          name='name'
          value={form.name}
          onChange={updateForm}></input>
        <br />
        <label htmlFor=''>Group color</label>
        <div>
          {colors.map(color => (
            <label key={color}>
              <input
                type='radio'
                name='color'
                value={color}
                checked={form.color === color}
                onChange={updateForm}
              />
              <span style={{ backgroundColor: color, width: '20px', height: '20px', display: 'inline-block' }}></span>
            </label>
          ))}
        </div>
        <br />
        <button type='submit'
          onChange={updateForm}>Create Group</button>
      </form>
  )
}

export default GroupForm
