import { useContext, useState, useEffect } from 'react'
import { GroupDataContext } from '../../Context/GroupContext'
import GroupRecords from '../../Components/Records/GroupRecords/GroupRecords'
import GroupForm from '../../Components/FormSection/GroupForm/GroupForms'
import NoteForm from '../../Components/FormSection/NoteForm/NoteForms'
import NoteRecords from '../../Components/Records/NoteRecords/NoteRecords'
import "./HomeStyles.css"

const Home = () => {
  const { selectedGroup } = useContext(GroupDataContext);
  const [showGroupForm, setShowGroupForm] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [mobileView, setMobileView] = useState('groups'); // 'groups' or 'notes'

  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth <= 768;
      setIsMobile(mobile);
      if (!mobile) {
        setMobileView('groups'); // Reset mobile view on desktop
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // When a group is selected on mobile, switch to notes view
  useEffect(() => {
    if (selectedGroup && isMobile) {
      setMobileView('notes');
    }
  }, [selectedGroup, isMobile]);

  // Reset to groups view when no group is selected on mobile
  useEffect(() => {
    if (!selectedGroup && isMobile) {
      setMobileView('groups');
    }
  }, [selectedGroup, isMobile]);

  const handleBackToGroups = () => {
    setMobileView('groups');
  };

  return (
    <section className='home-section'>
      <div className={`sidebar ${isMobile && mobileView === 'notes' ? 'mobile-hidden' : ''}`}>
        <div className='sidebar-header'>
          <h1>Pocket Notes</h1>
        </div>
        <div className='groups-section'>
          <GroupRecords />
          <button
            className='add-group-btn'
            onClick={() => setShowGroupForm(true)}
          >
            +
          </button>
        </div>
      </div>

      {(!isMobile || (isMobile && mobileView === 'notes' && selectedGroup)) && (
        <div className='main-content'>
          {selectedGroup ? (
            <div className="selected-group-section">
              <div className="group-header">
                {isMobile && (
                  <button className="back-button" onClick={handleBackToGroups}>
                    ←
                  </button>
                )}
                <div
                  className="group-avatar"
                  style={{backgroundColor: selectedGroup.color}}
                >
                  {selectedGroup.name.trim().split(' ').map(word => word[0]).join('').toUpperCase().slice(0, 2)}
                </div>
                <h2>{selectedGroup.name}</h2>
              </div>

              <div className="notes-display">
                <NoteRecords />
              </div>

              <div className="note-input-section">
                <NoteForm />
              </div>
            </div>
          ) : !isMobile ? (
            <div className="welcome-screen">
              <div className="welcome-content">
                <div className="welcome-illustration">
                  {/* You can add an illustration/image here */}
                  <div className="notes-icon">📝</div>
                </div>
                <h2>Pocket Notes</h2>
                <p>
                  Send and receive messages without keeping your phone online.<br/>
                  Use Pocket Notes on up to 4 linked devices and 1 mobile phone
                </p>
              </div>
              <div className="encryption-notice">
                🔒 end-to-end encrypted
              </div>
            </div>
          ) : null}
        </div>
      )}

      {showGroupForm && (
        <div className="modal-overlay" onClick={() => setShowGroupForm(false)}>
          <div onClick={(e) => e.stopPropagation()}>
            <GroupForm />
          </div>
        </div>
      )}
    </section>
  )
}

export default Home
