import {useContext} from "react"
import axios from 'axios';
import { GroupDataContext } from '../../../Context/GroupContext';

const GroupForm = () => {

    const {form, setform,getGroups} = useContext(GroupDataContext)

  const updateForm = (e) => {
    const { name, value } = e.target;

    setform({
      ...form,
      [name]: value
    });
  }
    const createGroup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/api/group", form)
      console.log(response)
      setform({
        name: "",
        color: "#FF5733"
      })
      getGroups();
    } catch (error) {
      console.log("Error details:", error.response?.data);
      console.log("Form data being sent:", form);
    }
  }
    const colors = ['#FF5733', '#33FF57', '#3357FF', '#FF33F5', '#33F5FF', '#F5FF33', '#FF8C00', '#8A2BE2'];
    return (
        <form onSubmit={createGroup}>
            <h1>Create a group</h1>
            <label htmlFor=''>Group name</label>
            <input type='text'
                name='name'
                value={form.name}
                onChange={updateForm}></input>
            <br />
            <label htmlFor=''>Group color</label>
            <div>
                {colors.map(color => (
                    <label key={color}>
                        <input
                            type='radio'
                            name='color'
                            value={color}
                            checked={form.color === color}
                            onChange={updateForm}
                        />
                        <span className="colour"></span>
                    </label>
                ))}
            </div>
            <br />
            <button type='submit'
                onChange={updateForm}>Create Group</button>
        </form>
    )
}

export default GroupForm
