import axios from 'axios';
import {useContext,useEffect} from "react"
import { GroupDataContext } from '../../../Context/GroupContext';
import './GroupRecordsStyles.css';

const GroupRecords = () => {

    const {groups, setGroups, selectGroup} = useContext(GroupDataContext)


    const getGroups = async () => {
        const allGroups = await axios.get("http://localhost:4000/api/group")
        const data = allGroups.data
        setGroups(data)
    }
    useEffect(() => {
        getGroups();
    }, []);
    return (
        <div>
            {groups && groups.map((group)=>(
                <div key={group._id} onClick={()=> selectGroup(group)}>
                    <h1>{group.name}</h1>
                    <h2>{group.color}</h2>
                </div>
            ))}
        </div>
    );
}

export default GroupRecords
