import { useContext } from 'react'
import axios from 'axios'
import { NoteDataContext } from '../../../Context/NoteContext'
import { GroupDataContext } from '../../../Context/GroupContext';
import "./NoteFormStyles.css";

const NoteForm = () => {
  const {noteForm, setNoteForm} = useContext (NoteDataContext)  // it was [notes,setNotes]
  const {selectedGroup} = useContext (GroupDataContext)

  const updateNoteForm = (e)=> {
    const {name,value} = e.target;
    setNoteForm({
      ...noteForm,
      [name]:value
    })

  }
  const createNote = async(e) => {
    e.preventDefault()
    try {
      const NoteData = {
        groupId: selectedGroup._id,
        content: noteForm.content
      };
      const response = await axios.post("http://localhost:4000/api/note",NoteData)
      console.log(response) // we will handle it later
      setNoteForm({
        groupId: "",
        content: ""
      });
    } catch(error) {
      console.log("Error creating note :" , error.response?.data);
    }
  }
  return (
    <form className='note-form' onSubmit={createNote}>
      <h1 className='form-title'>add note to {selectedGroup?.name}</h1>
      <label className='form-label'>Note Content</label>
      <textarea
      name='content'
      className='form-textarea'
      placeholder='Enter yourr text here'
      value={noteForm.content}
      onChange={updateNoteForm}
      rows={4}
      required
      />
      <button type='submit' className='submit-button'>add note</button>
    </form>
  )
}

export default NoteForm;
