import axios from 'axios';
import {useContext,useEffect} from "react"
import { GroupDataContext,selectGroup } from '../../../Context/GroupContext';
import './GroupRecordsStyles.css';

const GroupRecords = () => {

    const {groups, setGroups, selectGroup,selectedGroup} = useContext(GroupDataContext)

    const getAbriviation = (name)=>{
    const words = name.trim().split(` `).filter(words => words.length > 0 );
    if (words.length === 1) {
        return words[0][0].toUpperCase();
    } else if(words.length >=2) {
        return (words[0][0] + words[1][0]).toUpperCase();
    }
    return ""
}


    const getGroups = async () => {
        const allGroups = await axios.get("http://localhost:4000/api/group")
        const data = allGroups.data
        setGroups(data)
    }
    useEffect(() => {
        getGroups();
    }, []);
    return (
        <div className="group-records-container">
            {groups && groups.map((group)=>(
                <div 
                key={group._id}
                className={`group-record-item ${selectedGroup?._id === group._id ? `selected`: ``}`}
                onClick={()=> selectGroup(group)}>
                    <div className="group-name">{group.name}</div>
                    <div className="group-color-indicator"
                        style={{backgroundColor: group.color}}
                        >
                            {getAbriviation(group.name)}
                        </div>
                </div>
            ))}
        </div>
    );
}

export default GroupRecords
