import { useEffect, useState } from 'react';
import './App.css';

import axios from "axios";
import Group from '../../server/models/GroupModel';

function App() {
  const [groups, setgroups] = useState(null);
  const [note, getnote] = useState(null);
  // get all groups
  const getGroups = async () => {
    const allGroups = await axios.get("http://localhost:4000/api/group")
    const data = allGroups.data
    setgroups(data)
  }

  useEffect(() => {
    getGroups();
  }, []);
  return (
    <div className="App">
      {groups && groups.map((groups) => {
        return (
          <div key = {groups._id}>
            <h1>{Group.name}</h1>
            <h2>{Group.color}</h2>
            <h2>{Group.updatedAt}</h2>
          </div>

        )
      })}

    </div>
  );
}

export default App;
